  const handleChange =(ev)=>{
props.handleFilter ( {
    value: ev.currentTarget.value,
    key: 'name',
});
};

return (
    <>
    <label className="form__label" htmlFor="name">
    Busca por personaje:
  </label>
  <input
    className="form__input-text"
    type="text"
    name="name"
    id="text"
    onChange={handleChange}
  />
  </>
)
}
export default FilterByName;
